    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // TODO add your handling code here:
        //creating key from input
        String k=x.read("Key.txt");
        String rows[]=k.split(";");
        dimention=rows.length;
        int key[][]=new int[dimention][dimention];
        for(int i=0;i<dimention;i++)
        {
            for(int j=0;j<dimention;j++)
            {
                String el[]=rows[i].split(",");
                key[i][j]=Integer.parseInt(el[j]);
            }
        }
        //key created
        //preparing message for decryption
        String pt=x.read("PlainText.txt");
        char parts[]=new char[dimention];
        if(pt.length()%dimention==0)
        {
            parts=pt.toCharArray();
            for(int i=0;i<parts.length;i++)
            {
                System.out.print(parts[i]);
            }
        }
        else
            jTextArea1.setText("adjust your message");
        //encrypting
        int h=0;
        int v=0;
        int temp[]=new int[dimention];
        char cypher[]=new char[pt.length()];
        String s="";
        for(int i=0;i<pt.length()/dimention;i++)
        {
            for(int j=0;j<rows.length;j++)
            {
                temp[j]=reinterpret(parts[h]);
                h++;
            }
            int c[]=multiply(key,temp);
            for(int j=0;j<rows.length;j++)
            {
                cypher[v]=interpret(c[j]);
                v++;
            }
        }
        for(int i=0;i<cypher.length;i++)
        {
            jTextArea1.setText("the cyper is  \n");
            s+=cypher[i];
            jTextArea1.append(s);
        }
    }//GEN-LAST:event_jButton1ActionPerformed

