    public int [] multiply(int k[][],int p[])
    {
        int a[]=new int[p.length];
        int sum=0;
        for(int x=0;x<a.length;x++)
        {
            for(int i=0;i<k.length;i++)
            {
                for(int j=0;j<p.length;j++)
                {
                    sum+=p[j]*k[j][i];
                }
                a[x]=modulo(sum,26);
                sum=0;
            }
        }
        return a;
    }
    int modulo(int x,int mod)
    {
        if(x>mod)
        {
            x=x%mod;
        }
        else if(x<0)
        {
            while(x<0)
                x+=mod;
        }
        return x;
    }
    char interpret(int x)
    {
        char c='a';
        for(int i=0;i<=26;i++)
        {
            if((x)==i)
            {
                c=(char)(c+i);
            }
        }
        return c ;
    }
    int reinterpret(char c)
    {
        int x=0;
        char y='a';
        for(int i=0;i<=26;i++)
        {
            if(c==(char)(y+i))
            {
                x=i;
            }
        }
        return x;
    }
